import { Button, Card, Col, Form, Image, InputNumber, Rate, Row } from "antd";
import classes from "./ProductDetail.module.scss";
import { Typography } from "antd";
import { AppstoreAddOutlined, ShoppingCartOutlined } from "@ant-design/icons";
import { productService } from "services/product.service";
import { useEffect, useState } from "react";

const { Title, Paragraph, Text } = Typography;
function ProductDetail(props) {
  const [data, setData] = useState(null);
  const { id } = props;
  useEffect( () => {
    productService.getProductId(id).then((x) => {
      setData(x.data);
    });
  }, []);
  return (
    <Card
      title="Chi tiết sản phẩm"
      //   extra={<a href="#">More</a>}
      style={{ width: "100%", borderRadius: "0px" }}
      bordered={true}
      //   loading={true}
    >
      {data && (
        <Row>
          <Col span={6} className={classes.productDetail}>
            <div>
              <Image
                src={data.imageUrl}
                fallback="data:image/png;base64,"
              />
            </div>
          </Col>
          <Col span={16}>
            <Typography>
              <Title level={4}>{data.name}</Title>
              <Paragraph>{data.detail}</Paragraph>
              {/* <Paragraph>
                After massive project practice and summaries, Ant Design, a
                design language for background applications, is refined by Ant
                UED Team, which aims to
                <Text strong>
                  uniform the user interface specs for internal background
                  projects, lower the unnecessary cost of design differences and
                  implementation and liberate the resources of design and
                  front-end development
                </Text>
                <Title level={3} type="danger">
                  {data.price}
                </Title>
                <Text delete strong>
                  {(data.price * data.promotion) / 100}
                </Text>
              </Paragraph> */}
              <div>
                <Text>Đánh giá:</Text>
                <Rate style={{ width: "25%" }} disabled />
                <Text>(31 người đánh giá)</Text>
              </div>
            </Typography>
            <Form>
              <Form.Item label="Số lượng ">
                <Form.Item name="input-number" noStyle>
                  <InputNumber defaultValue={1} min={1} max={5} />
                </Form.Item>
              </Form.Item>
              <Form.Item>
                <Button
                  icon={<AppstoreAddOutlined />}
                  style={{ margin: "5px" }}
                  htmlType="submit"
                >
                  Mua ngay
                </Button>
                <Button
                  icon={<ShoppingCartOutlined />}
                  style={{ margin: "5px" }}
                >
                  Thêm giỏ hàng
                </Button>
              </Form.Item>
            </Form>
          </Col>
        </Row>
      )}
    </Card>
  );
}
export default ProductDetail;
